import{_ as e,o as t,c as a,R as i}from"./chunks/framework.JncSVmEt.js";const b=JSON.parse('{"title":"Accessibility","description":"","frontmatter":{},"headers":[],"relativePath":"accessibility.md","filePath":"accessibility.md","lastUpdated":1662562836000}'),r={name:"accessibility.md"},s=i('<h1 id="accessibility" tabindex="-1">Accessibility <a class="header-anchor" href="#accessibility" aria-label="Permalink to &quot;Accessibility&quot;">​</a></h1><p><a href="https://chimein2.cla.umn.edu/" target="_blank" rel="noreferrer">ChimeIn</a> is created and maintained by the <a href="https://cla.umn.edu/" target="_blank" rel="noreferrer">College of Liberal Arts</a> at the <a href="https://twin-cities.umn.edu/" target="_blank" rel="noreferrer">University of Minnesota</a>.</p><h2 id="mission-statement" tabindex="-1">Mission Statement <a class="header-anchor" href="#mission-statement" aria-label="Permalink to &quot;Mission Statement&quot;">​</a></h2><p>We believe that creating inclusive and equitable learning environments improves teaching and learning for everyone. Our goal is make ChimeIn accessible to all participants and presenters.</p><p>We:</p><ul><li>commit to providing equitable access to all users,</li><li>strive to meet W3C WCAG 2.1 level AA and ARIA 1 guidelines,</li><li>work to identify and fix accessibility issues in a timely manner</li></ul><h2 id="technical-and-accessibility-support" tabindex="-1">Technical and Accessibility Support <a class="header-anchor" href="#technical-and-accessibility-support" aria-label="Permalink to &quot;Technical and Accessibility Support&quot;">​</a></h2><p>For ChimeIn help documentation, tap <a href="https://umn-latis.github.io/ChimeIn2.0/" target="_blank" rel="noreferrer">Help</a> in the main menu.</p><p>Users experiencing technical or accessibility issues may contact <a href="mailto:help@umn.edu" target="_blank" rel="noreferrer">help@umn.edu</a>.</p><p>ChimeIn is an open source project that welcomes community participation. Interested users can track development work, submit issues, and share feature requests at <a href="https://github.com/UMN-LATIS/ChimeIn2.0" target="_blank" rel="noreferrer">https://github.com/UMN-LATIS/ChimeIn2.0</a>.</p><h2 id="best-practices-for-presenters" tabindex="-1">Best Practices for Presenters <a class="header-anchor" href="#best-practices-for-presenters" aria-label="Permalink to &quot;Best Practices for Presenters&quot;">​</a></h2><ul><li>Plain text can be read by assistive devices. Do not use screenshots of text.</li><li>Images with alternative text or captions can be read by assistive devices. Include alt text on all images.</li><li>The image submission question type allows participants to add image descriptions which can be read by screen readers. Ask your participants to include an image description with their submission. Model this behavior for users.</li></ul><h2 id="voluntary-product-accessibility-template-vpat" tabindex="-1">Voluntary Product Accessibility Template (VPAT) <a class="header-anchor" href="#voluntary-product-accessibility-template-vpat" aria-label="Permalink to &quot;Voluntary Product Accessibility Template (VPAT)&quot;">​</a></h2><p>A <a href="https://www.section508.gov/sell/vpat/" target="_blank" rel="noreferrer">Voluntary Product Accessibility Template (VPAT)</a> is a document that explains how information and communication technology (ICT) products conform to the Revised 508 Standards for IT accessibility.</p><p><strong>Name of Product:</strong> ChimeIn</p><p><strong>Product Description:</strong> ChimeIn is a live polling tool for partipant engagement.</p><p><strong>Last VPAT Update:</strong> August 2022</p><p><strong>Contact Information:</strong> <a href="mailto:latistecharch@umn.edu" target="_blank" rel="noreferrer">latistecharch@umn.edu</a></p><p><strong>Download:</strong> <a href="https://github.com/UMN-LATIS/ChimeIn2.0/raw/develop/docs/chimein-vpat-rev202208.pdf" target="_blank" rel="noreferrer">ChimeIn VPAT</a></p><h2 id="resources" tabindex="-1">Resources <a class="header-anchor" href="#resources" aria-label="Permalink to &quot;Resources&quot;">​</a></h2><ul><li><a href="https://accessibility.umn.edu/what-you-can-do/start-7-core-skills/alternative-text" target="_blank" rel="noreferrer">Make Images Accessible with Alternative Text</a></li><li><a href="https://policy.umn.edu/it/webaccess" target="_blank" rel="noreferrer">UMN Accessibility Policy Statement</a></li><li><a href="https://accessibility.umn.edu/" target="_blank" rel="noreferrer">Accessible U</a></li></ul><h2 id="known-issues" tabindex="-1">Known Issues <a class="header-anchor" href="#known-issues" aria-label="Permalink to &quot;Known Issues&quot;">​</a></h2><ul><li>The image heatmap question type is likely to be challenging for participants with visual impairment or fine motor impairments.</li></ul>',23),n=[s];function o(l,c,h,p,u,m){return t(),a("div",null,n)}const f=e(r,[["render",o]]);export{b as __pageData,f as default};
