import{_ as n,c as r,a4 as s,j as e,a as t,o}from"./chunks/framework.QfYtEqBg.js";const i="/ChimeIn2.0/assets/faq-manual-adjustment-excusing.D5Yef7Pu.png",l="/ChimeIn2.0/assets/faq-manual-adjustment-new-col.C9vT7JLe.png",d="/ChimeIn2.0/assets/faq-before-curve.D-mTM1B2.png",c="/ChimeIn2.0/assets/faq-after-curve.D5VecImS.png",u="/ChimeIn2.0/assets/faq-select-curve-grades._SFd97Gx.png",m="/ChimeIn2.0/assets/faq-grade-curve-settings.CYtODArH.png",h="/ChimeIn2.0/assets/faq-force-sync-folder.Rxre5xqI.png",g="/ChimeIn2.0/assets/faq-force-sync-chime.DuIJTW7f.png",b=JSON.parse('{"title":"FAQ: Canvas Grading","description":"","frontmatter":{},"headers":[],"relativePath":"grading-faq.md","filePath":"grading-faq.md","lastUpdated":1737751984000}'),p={name:"grading-faq.md"};function f(y,a,C,w,_,I){return o(),r("div",null,a[0]||(a[0]=[s('<h1 id="faq-canvas-grading" tabindex="-1">FAQ: Canvas Grading <a class="header-anchor" href="#faq-canvas-grading" aria-label="Permalink to &quot;FAQ: Canvas Grading&quot;">​</a></h1><details id="faq-manual-adjust-chimein-grades"><summary>How can I manually adjust ChimeIn grades?</summary><p>You might have a student with an excused absence, or may want to round up some ChimeIn grades. You can tweak ChimeIn grades in the Canvas gradebook a couple of different ways:</p><h3 id="edit-canvas-grades-directly" tabindex="-1">Edit Canvas Grades Directly <a class="header-anchor" href="#edit-canvas-grades-directly" aria-label="Permalink to &quot;Edit Canvas Grades Directly&quot;">​</a></h3><p>Probably the easiest to <strong>edit the grades directly</strong> in the Canvas gradebook, with a few caveats. ChimeIn automatically re-syncs assignments a few hours after any new responses, so be sure to close questions to avoid new responses which will overwrite your edits.</p><p>For example, I could type &quot;ex&quot; in the grade column to excuse a student from the ChimeIn assignment:</p><img src="'+i+'" width="400" alt="manual adjusting the gradebook example. Typing &#39;ex&#39; will directly in the column will excuse the student from an assignment."><div class="important custom-block github-alert"><p class="custom-block-title">IMPORTANT</p><p>ChimeIn will resync an assignment if there are any new responses, so wait until all your responses are in and your question is closes before making adjustments.</p></div><h3 id="separate-column-for-adjustments" tabindex="-1">Separate Column for Adjustments <a class="header-anchor" href="#separate-column-for-adjustments" aria-label="Permalink to &quot;Separate Column for Adjustments&quot;">​</a></h3><p>Another option is to <strong>create a separate grade column</strong> specifically for any ChimeIn point adjustments, and then assign points as needed to that column. Since this is a separate column, you don&#39;t need to worry about ChimeIn syncing overwriting your changes.</p><img src="'+l+'" alt="The Canvas gradebook contains a column for ChimeIn responses, and another separate column for ChimeIn adjustments. The first user earned 7.5/10 points for ChimeIn, but the instructor rounded up their score to full points by adding another 2.5 points to the ChimeIn adjustments column." width="400"></details><details id="faq-round-assignment-to-full"><summary>How can I round up assignment scores to 100%?</summary><p>You can use <a href="https://community.canvaslms.com/t5/Instructor-Guide/How-do-I-curve-grades-in-the-Gradebook/ta-p/745" target="_blank" rel="noreferrer">Curve Grades</a> in Canvas to do the adjustment.</p><p>This will give any participation full points, and non-participants a zero:</p><table tabindex="0"><thead><tr><th>Before</th><th>After</th></tr></thead><tbody><tr><td><img src="'+d+'" alt="Before Curve: scores are 7.5/10, 6/10, and -/10 (indicating no score)" width="300"></td><td><img src="'+c+'" alt="After Curve: 10/10, 10/10, and 0/10" width="300"></td></tr></tbody></table><ol><li>Go to Your Course &gt; Grades.</li><li>Find the ChimeIn assignment you wish to adjust, and click the ⋮ next to the assignment name.</li><li>Choose &quot;Curve Grades&quot; from the menu: <img src="'+u+'" alt="Curve Grades shown in drop-down grade menu" width="300"></li><li>Set your curve average as full points (e.g. 10/10), and check the assign 0&#39;s to non-participants box. <img src="'+m+'" alt="Curve Grade Settings" width="300"></li><li>Apply the settings by clicking the &quot;Curve Grades&quot; button.</li></ol></details>',3),e("details",{id:"faq-drop-lowest"},[e("summary",null,"How can I drop lowest ChimeIn assignment scores?"),e("p",null,"If you set up a ChimeIn assignment for each class meeting in Canvas, you may want to permit students to miss a certain number of before it impacts their grades."),e("p",null,[t("You can use "),e("a",{href:"https://community.canvaslms.com/t5/Instructor-Guide/How-do-I-add-an-assignment-group-in-a-course/ta-p/970",target:"_blank",rel:"noreferrer"},"Canvas assignment groups"),t(" to group all your chime assignments together, and then "),e("a",{href:"https://community.canvaslms.com/t5/Instructor-Guide/How-do-I-create-rules-for-an-assignment-group/ta-p/848",target:"_blank",rel:"noreferrer"},"create a rule to drop the lowest X chimes"),t(".")]),e("p",null,"Here's a demo:"),e("iframe",{width:"100%",height:"420",src:"https://www.youtube.com/embed/RXczYrezKmY",title:"YouTube video player",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture",allowfullscreen:""})],-1),s('<details id="faq-how-long-until-sync"><summary>How long does it take for ChimeIn to sync grades with Canvas?</summary><p>ChimeIn will automatically sync any assignment with new responses <strong>3 hours after the last response</strong>.</p></details><details id="faq-manual-resync-grades"><summary>How can I manually resync grades in Canvas with ChimeIn?</summary><p>You can manually resync grades in ChimeIn.</p><p>If you setup ChimeIn for <strong>Multiple Grade Columns (default)</strong>:</p><ol><li>Go to <a href="https://chimein.cla.umn.edu" target="_blank" rel="noreferrer">ChimeIn</a> and choose your course.</li><li>Choose the folder (assignment) you wish to sync.</li><li>Within the folder, click <strong>Folder Settings</strong> to open the settings menu, then choose <strong>Force Sync with Canvas</strong>:</li></ol><img src="'+h+'" alt="Force Sync Folder" width="400"><p>If you setup ChimeIn for <strong>One Grade Column</strong></p><ol><li>Go to <a href="https://chimein.cla.umn.edu" target="_blank" rel="noreferrer">ChimeIn</a> and choose your course.</li><li>Choose <strong>Chime Settings</strong> to open the settings menu, then click <strong>Force Sync</strong>:</li></ol><img src="'+g+'" alt="Force Sync Chime" width="400"></details>',2)]))}const q=n(p,[["render",f]]);export{b as __pageData,q as default};
